mapper.select:
SELECT LCOMMENT_ID, LREVIEW_ID, LMEMBER_ID, REVIEW_SEQ, REVIEW_LEV, COMMENT_DATE, COMMENT_CONTENT, LEVENT_ID, MEMBER_REALID, MBPROFILE_URL, LOCKCHECK FROM VIEW_COMMENT
LEVENT_ID = #{levent_id, jdbcType=VARCHAR}
ORDER BY REVIEW_SEQ

SELECT LREVIEW_ID, REVIEW_DATE, REVIEW_CONTENT, LMEMBER_ID, MEMBER_REALID, LEVENT_ID, MBPROFILE_URL, LOCKCHECK FROM VIEW_REVIEW
LEVENT_ID = #{levent_id, jdbcType=VARCHAR}
ORDER BY LREVIEW_ID

SELECT COUNT(E.EVEATTEND_ID) - COUNT(R.LREVIEW_ID) AS POWER
FROM EVEATTEND E LEFT OUTER JOIN REVIEW R
  ON E.EVEATTEND_ID = R.EVEATTEND_ID
WHERE LEVENT_ID=#{levent_id, jdbcType=VARCHAR}
  AND ATTEND_ID=1
  AND LMEMBER_ID=#{id, jdbcType=VARCHAR}
